import { useState } from "react";
import "../estilos/GameRules.css";

function GameRules({ onComplete }) {
  const [currentPage, setCurrentPage] = useState(0);
  const [rules] = useState([
    { text: "Regla 1: Usa las teclas para moverte.", image: "/images/regla1.jpg" },
    { text: "Regla 2: Completa el nivel para ganar.", image: "/images/regla2.jpg" }
  ]);

  const handleNext = () => {
    if (currentPage < rules.length - 1) {
      setCurrentPage(currentPage + 1);
    } else {
      onComplete();
    }
  };

  const handleSkip = () => {
    onComplete();
  };

  return (
    <div className="game-rules">
      <h2>Reglas del Juego</h2>
      <div className="rule">
        <p>{rules[currentPage].text}</p>
        <img src={rules[currentPage].image} alt="Regla" className="rule-image" />
      </div>

      <div className="buttons">
        <Button onClick={handleNext} variant="primary">Siguiente</Button>
        <Button onClick={handleSkip} variant="secondary">Saltar y Jugar</Button>
      </div>
    </div>
  );
}

export default GameRules;
