import "../App.css";

function TaskItem({ task, index, removeTask, moveTaskUp, moveTaskDown }) {
    return (
      <li className="list-group-item d-flex align-items-center">
        <span id="images" className="flex-grow-1">{task}</span>
        <div className="task-icons">
          <img src="/images/taskUp.png" width="30" className="task-icon" alt="Up" onClick={() => moveTaskUp(index)} />
          <img src="/images/taskDown.png" width="30" className="task-icon" alt="Down" onClick={() => moveTaskDown(index)} />
          <img src="/images/removeTask.png" width="30" className="task-icon" alt="Delete" onClick={() => removeTask(index)} />
        </div>
      </li>
    );
  }
  
export default TaskItem;
