import { useRef } from "react";

function TaskInput({ addTask }) {
  const taskInputRef = useRef(null);

  const handleAddTask = () => {
    const taskName = taskInputRef.current.value.trim();
    if (taskName === "") {
      alert("Por favor, pon el nombre de alguna tarea para añadir.");
      return;
    }
    addTask(taskName);
    taskInputRef.current.value = "";
  };

  return (
    <div className="input-group mb-3">
      <input ref={taskInputRef} type="text" className="form-control" placeholder="Pon una tarea..." />
      <button className="btn btn-primary" onClick={handleAddTask}>Añadir Tarea</button>
    </div>
  );
}

export default TaskInput;
