import { useState } from "react";

function TaskInput({ addTask }) {
  const [taskInput, setTaskInput] = useState("");

  const handleInputChange = (e) => {
    setTaskInput(e.target.value);
    //console.log("taskInput actualizado:", e.target.value);
  };

  const handleAddTasks = () => {
    const tasks = taskInput.split(",").map((task) => task.trim());

    tasks.forEach((task) => {
      if (task.length <= 30) {
        addTask(task);
      } else {
        alert("No te pases con el nombre.");
      }
    });

    setTaskInput("");
  };

  return (
    <div>
      <input
        type="text"
        value={taskInput}
        onChange={handleInputChange}
        placeholder="Escribe las tareas"
      />
      <button onClick={handleAddTasks}>Agregar Tareas</button>
    </div>
  );
}

export default TaskInput;
