import { useState } from "react";
import { useSound } from "./componentes/SoundContext";
import Loader from "./componentes/Loader";
import MainMenu from "./componentes/MainMenu";
import SettingsMenu from "./componentes/Settings";
import "./App.css";

function App() {
  const [loading, setLoading] = useState(true);
  const [screen, setScreen] = useState("menu"); // Estado para manejar la navegación
  const { volume, setVolume, effects, setEffects, music, setMusic } = useSound();

  if (loading) {
    return <Loader onComplete={() => setLoading(false)} />;
  }

  return (
    <div className="App">
      <header className="App-header">
        {screen === "game" && <h2>Aquí iría la pantalla del juego</h2>}
        {screen === "settings" && <SettingsMenu onBack={() => setScreen("menu")} />}
        {screen === "menu" && <MainMenu onStartGame={() => setScreen("game")} onOpenSettings={() => setScreen("settings")} />}
      </header>
      <audio
        src="path_to_game_music.mp3"
        autoPlay
        loop
        volume={music / 100} // Usa el volumen de la música que se ajusta en los ajustes
      />
    </div>
  );
}

export default App;
