import logo from './logo.svg';
import './App.css';
import { useState, useRef } from 'react';

function App() {
  const [tasks, setTasks] = useState([]);
  const taskInputRef = useRef(null);

  const addNewTask = () => {
    const taskName = taskInputRef.current.value.trim();
    if (taskName === "") {
      alert("Please enter a task name.");
      return;
    }

    setTasks([...tasks, taskName]); // Agregar nueva tarea
    taskInputRef.current.value = ""; // Limpiar input
  };

  const removeTask = (index) => {
    setTasks(tasks.filter((_, i) => i !== index));
  };

  const moveTaskUp = (index) => {
    if (index === 0) return;
    const updatedTasks = [...tasks];
    [updatedTasks[index], updatedTasks[index - 1]] = [updatedTasks[index - 1], updatedTasks[index]];
    setTasks(updatedTasks);
  };

  const moveTaskDown = (index) => {
    if (index === tasks.length - 1) return;
    const updatedTasks = [...tasks];
    [updatedTasks[index], updatedTasks[index + 1]] = [updatedTasks[index + 1], updatedTasks[index]];
    setTasks(updatedTasks);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h2>To-Do List</h2>
        <div className="input-group mb-3">
          <input ref={taskInputRef} type="text" className="form-control" placeholder="Enter task..." />
          <button className="btn btn-primary" onClick={addNewTask}>Add Task</button>
        </div>
        <ul className="list-group">
          {tasks.map((task, index) => (
            <li key={index} className="list-group-item d-flex align-items-center">
              <span className="flex-grow-1">{task}</span>
              <img src="images/taskUp.png" width="10" alt="Up" onClick={() => moveTaskUp(index)} />
              <img src="images/taskDown.png" alt="Down" onClick={() => moveTaskDown(index)} />
              <img src="images/removeTask.png" alt="Delete" onClick={() => removeTask(index)} />
            </li>
          ))}
        </ul>
      </header>
    </div>
  );
}

export default App;
