import { useState } from "react";
import Button from "./Button";

function SettingsMenu({ onBack }) {
  const [volume, setVolume] = useState(50);
  const [effects, setEffects] = useState(50);
  const [music, setMusic] = useState(50);

  return (
    <div className="App-header">
      <h1>Ajustes</h1>

      <div className="settings-group">
        <label>Volumen General</label>
        <input
          type="range"
          min="0"
          max="100"
          value={volume}
          onChange={(e) => setVolume(e.target.value)}
        />
      </div>

      <div className="settings-group">
        <label>Volumen de Efectos</label>
        <input
          type="range"
          min="0"
          max="100"
          value={effects}
          onChange={(e) => setEffects(e.target.value)}
        />
      </div>

      <div className="settings-group">
        <label>Volumen de MÃºsica</label>
        <input
          type="range"
          min="0"
          max="100"
          value={music}
          onChange={(e) => setMusic(e.target.value)}
        />
      </div>

      <Button onClick={onBack}>Volver</Button>

      <img src={"/images/settings.svg"} className="settings-button-img" alt="settingsButton" />
    </div>
  );
}

export default SettingsMenu;
