import { useState, useEffect } from "react";
import TaskInput from "./componentes/TaskInput";
import TaskList from "./componentes/TaskList";
import Loader from "./componentes/Loader";
import "./App.css";

function App() {
  const [progress, setProgress] = useState(0);
  const [loading, setLoading] = useState(true);
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    let interval;
    if (progress < 100) {
      interval = setInterval(() => {
        setProgress((oldProgress) => {
          const newProgress = oldProgress + 100 / 30; // 100% in 3 seconds
          return newProgress >= 100 ? 100 : newProgress;
        });
      }, 100);
    } else {
      clearInterval(interval);
      setTimeout(() => setLoading(false), 300); // Small delay to complete animation
    }
    return () => clearInterval(interval);
  }, [progress, setLoading]);

  const addTask = (taskName) => setTasks([...tasks, taskName]);
  const removeTask = (index) => setTasks(tasks.filter((_, i) => i !== index));

  const moveTaskUp = (index) => {
    if (index === 0) return;
    const updatedTasks = [...tasks];
    [updatedTasks[index], updatedTasks[index - 1]] = [updatedTasks[index - 1], updatedTasks[index]];
    setTasks(updatedTasks);
  };
  const moveTaskDown = (index) => {
    if (index === tasks.length - 1) return;
    const updatedTasks = [...tasks];
    [updatedTasks[index], updatedTasks[index + 1]] = [updatedTasks[index + 1], updatedTasks[index]];
    setTasks(updatedTasks);
  };

  if (loading) {
    return <Loader setLoading={setLoading} />;
  }

  return (
    <div className="loader">
      <img src="/images/logo.png" width="300" className="App-logo" alt="React Logo" />
      <p>Cargando...</p>
      <div className="progress-bar-container" style={{ width: "100%", background: "#ddd", borderRadius: "8px", overflow: "hidden", marginTop: "10px" }}>
        <div className="progress-bar" style={{ width: `${progress}%`, height: "10px", background: "#4caf50", transition: "width 0.1s linear" }}></div>
      </div>
    </div>
  );
}

export default App;
