import { useState, useEffect } from "react";

function Loader({ onComplete }) {
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const interval = 10; // Actualizacion cada 50 ms
    const totalDuration = 3000; // Duracion de 3000ms -> 3 segundos
    const step = (100 / (totalDuration / interval)); // Cuánto aumenta cada vez

    const timer = setInterval(() => {
      setProgress((prev) => {
        const nextProgress = prev + step;
        return nextProgress >= 100 ? 100 : nextProgress;
      });
    }, interval);

    setTimeout(() => {
      clearInterval(timer);
      onComplete(); // Llamamos a onComplete cuando termine la carga
    }, totalDuration);

    return () => clearInterval(timer);
  }, [onComplete]);

  return (
    <div className="loader">
      <img src="/images/logo.png"  width="300" className="App-logo" alt="React Logo" />
      <h2>Cargando...</h2>
      <div
        className="progress-bar-container"
        style={{
          width: "30%",
          background: "#ddd",
          borderRadius: "8px",
          overflow: "hidden",
          marginTop: "10px",
        }}
      >
        <div
          className="progress-bar"
          style={{
            width: `${progress}%`,
            height: "10px",
            background: "#4caf50",
            transition: "width 0.05s linear",
          }}
        ></div>
      </div>
    </div>
  );
}

export default Loader;
