import { useState } from "react";
import { useSound } from "./componentes/SoundContext";
import Loader from "./componentes/Loader";
import MainMenu from "./componentes/MainMenu";
import SettingsMenu from "./componentes/SettingsMenu";
import GameRules from "./componentes/GameRules";
import Caja from "./componentes/Caja";
import SaveSlotsMenu from "./componentes/SaveSlotsMenu";
import "./App.css";

function App() {
  const [loading, setLoading] = useState(true);
  const [screen, setScreen] = useState("menu");
  const [currentSlot, setCurrentSlot] = useState(null);
  const [saveMode, setSaveMode] = useState(null); // 'new' o 'load'

  const { music } = useSound();

  if (loading) {
    return <Loader onComplete={() => setLoading(false)} />;
  }

  const handleStartFromSlot = (slotIndex) => {
    setCurrentSlot(slotIndex);
    if (saveMode === "new") {
      setScreen("rules");
    } else if (saveMode === "load") {
      setScreen("game");
    }
  };

  const handleGameCompleteRules = () => {
    setScreen("game");
  };

  return (
    <div className="App">
      <header className="App-header">
        {screen === "rules" && (
          <GameRules
            onBackToMenu={() => setScreen("menu")}
            onComplete={handleGameCompleteRules}
          />
        )}
        {screen === "game" && <Caja slotIndex={currentSlot} />}
        {screen === "settings" && (
          <SettingsMenu onBack={() => setScreen("menu")} />
        )}
        {screen === "menu" && (
          <MainMenu
            onNewGame={() => {
              setSaveMode("new");
              setScreen("saveslots");
            }}
            onLoadGame={() => {
              setSaveMode("load");
              setScreen("saveslots");
            }}
            onOpenSettings={() => setScreen("settings")}
          />
        )}
        {screen === "saveslots" && (
          <SaveSlotsMenu
            mode={saveMode}
            onBack={() => setScreen("menu")}
            onStartGame={handleStartFromSlot}
          />
        )}
      </header>
      <audio src="path_to_game_music.mp3" autoPlay loop volume={music / 100} />
    </div>
  );
}

export default App;
