import { useState } from "react";
import Loader from "./componentes/Loader";
import MainMenu from "./componentes/MainMenu";
import SettingsMenu from "./componentes/Settings";
import "./App.css";

function App() {
  const [loading, setLoading] = useState(true);
  const [screen, setScreen] = useState("menu"); // Estado para manejar la navegación

  if (loading) {
    return <Loader onComplete={() => setLoading(false)} />;
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Tortilla's Croqueteria</h1>
      </header>

      {/* Usando la rejilla de Bootstrap */}
      <div className="container text-center mt-5">
        <div className="row">
          <div className="col-12 col-md-6 mb-4">
            <Button onClick={() => console.log('Jugar')}>Jugar</Button>
          </div>
          <div className="col-12 col-md-6 mb-4">
            <Button onClick={() => console.log('Ajustes')}>Ajustes</Button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
