import { useState, useEffect } from "react";
import TaskInput from "./componentes/TaskInput";
import TaskList from "./componentes/TaskList";
import Loader from "./componentes/Loader";
import MainMenu from "./componentes/MainMenu";
import "./App.css";

function App() {
  const [loading, setLoading] = useState(true);
  const [tasks, setTasks] = useState([]);
  const [screen, setScreen] = useState("menu"); // Estado para manejar la navegación

  if (loading) {
    return <Loader onComplete={() => setLoading(false)} />;
  }

  if (screen === "menu") {
    return <MainMenu onStartGame={() => setScreen("game")} onOpenSettings={() => setScreen("settings")} />;
  }

  const addTask = (taskName) => setTasks([...tasks, taskName]);
  const removeTask = (index) => setTasks(tasks.filter((_, i) => i !== index));

  const moveTaskUp = (index) => {
    if (index === 0) return;
    const updatedTasks = [...tasks];
    [updatedTasks[index], updatedTasks[index - 1]] = [updatedTasks[index - 1], updatedTasks[index]];
    setTasks(updatedTasks);
  };
  const moveTaskDown = (index) => {
    if (index === tasks.length - 1) return;
    const updatedTasks = [...tasks];
    [updatedTasks[index], updatedTasks[index + 1]] = [updatedTasks[index + 1], updatedTasks[index]];
    setTasks(updatedTasks);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h2>Lista de Tareas</h2>
        <TaskInput addTask={addTask} />
        <TaskList tasks={tasks} removeTask={removeTask} moveTaskUp={moveTaskUp} moveTaskDown={moveTaskDown} />
      </header>
    </div>
  );
}

export default App;
