import { useState } from "react";
import "../estilos/GameRules.css";

function GameRules({ onComplete }) {
  const rules = [
    {
      text: "Bienvenido a Tortilla's Croqueteria! Aquí aprenderás cómo jugar.",
      image: "/images/rule1.png",
    },
    {
      text: "Haz clic en los ingredientes correctos para hacer la mejor tortilla!", 
      image: "/images/rule2.png",
    },
  ];

  const [currentPage, setCurrentPage] = useState(0);

  return (
    <div className="App-header">
      <h1>Reglas del Juego</h1>
      <p>{rules[currentPage].text}</p>
      <img src={rules[currentPage].image} alt="Regla" className="rule-image" />
      <div className="rules-buttons">
        {currentPage < rules.length - 1 ? (
          <button onClick={() => setCurrentPage(currentPage + 1)}>Siguiente</button>
        ) : (
          <button onClick={onComplete}>Jugar</button>
        )}
        <button onClick={onComplete}>Saltar y Jugar</button>
      </div>
    </div>
  );
}

export default GameRules;
